import api from '@/api/api';
import { useSnackBar } from '@/stores/snackBar.js';
export default {
  name: 'AddSubject',
  props: {
    schoolCourseId: {
      type: Number,
      default: null
    }
  },
  data: () => ({
    loading: false,
    dialogAddSubject: false,
    subjects: []
  }),
  computed: {},
  watch: {},
  async created() {
    await this.listSubjects();
  },
  methods: {
    close() {
      this.dialogAddSubject = false;
    },
    async listSubjects() {
      this.loading = true;
      try {
        this.subjects = await api.listSubjects();
        this.loading = false;
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    },
    async addSubject(subjectId) {
      this.loading = true;
      const data = {
        school_course_id: this.schoolCourseId,
        subject_id: subjectId
      };
      try {
        await api.createSchoolCourseSubject(data);
        this.useSnackBar.increment({
          status: true,
          msg: 'matéria incluida com sucesso'
        });
        this.$emit('reload');
        this.loading = false;
        this.close();
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
