import api from '@/api/api';
export default {
  name: 'EditSubject',
  props: {
    subjectId: {
      type: Number,
      default: null
    },
    subjectName: {
      type: String,
      default: ''
    },
    dialogEditSubject: {
      type: Boolean,
      default: false
    }
  },
  data: () => ({
    loading: false,
    dialog: false,
    name: ''
  }),
  computed: {},
  watch: {
    subjectName() {
      this.name = this.subjectName;
    }
  },
  created() {
    this.name = this.subjectName;
  },
  methods: {
    close() {
      this.$emit('close');
    },
    async update() {
      this.loading = true;
      const data = {
        name: this.name
      };
      try {
        await api.updateSubject(this.subjectId, data);
        this.$emit('reload');
        this.loading = false;
        this.$emit('close');
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
