import axios from '@/api/axios';
export default {
  name: 'App',
  components: {},
  async created() {
    this.setupAxiosToken();
  },
  methods: {
    setupAxiosToken() {
      if (localStorage.getItem('token')) {
        axios.defaults.headers.common.Authorization = `Bearer ${localStorage.getItem('token')}`;
      }
    }
  }
};
