import api from '@/api/api';
import { useSnackBar } from '@/stores/snackBar.js';
export default {
  name: 'EditStudents',
  props: {
    studentId: {
      type: Number,
      default: null
    },
    studentName: {
      type: String,
      default: ''
    },
    dialogEditStudent: {
      type: Boolean,
      default: false
    }
  },
  data: () => ({
    loading: false,
    dialog: false,
    name: ''
  }),
  computed: {},
  watch: {
    studentName() {
      this.name = this.studentName;
    }
  },
  created() {
    this.name = this.studentName;
  },
  methods: {
    close() {
      this.$emit('close');
    },
    async update() {
      this.loading = true;
      const data = {
        name: this.name
      };
      try {
        await api.updateStudent(this.studentId, data);
        this.$emit('reload');
        this.loading = false;
        this.$emit('close');
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
