import { defineStore } from 'pinia'

export const useSnackBar = defineStore('snackBar', {
  state() {
    return {
      show: false,
      msg: ''
    }
  },

  actions: {
    increment(value) {
      this.$state.show = value.status
      this.$state.msg = value.msg
    }
  },

  getters: {
    showSnackBar() {
      return this.show
    },
    msgSnackBar() {
      return this.msg
    }
  }
})
