import api from '@/api/api';
export default {
  name: 'AddStudent',
  props: {
    schoolCourseId: {
      type: Number,
      default: null
    }
  },
  data: () => ({
    loading: false,
    dialogAddStudent: false,
    students: []
  }),
  async created() {
    await this.listStudents();
  },
  methods: {
    close() {
      this.dialogAddStudent = false;
    },
    async listStudents() {
      this.loading = true;
      try {
        this.students = await api.listStudents();
        this.loading = false;
      } catch (e) {
        const feedback = e?.response?.data?.message ?? 'Ocorreu um erro, tente novamente mais tarde';
        this.snackBar.increment({
          show: true,
          msg: feedback,
          color: 'warning'
        });
        this.loading = false;
      }
    },
    async addStudent(studentId) {
      this.loading = true;
      const data = {
        school_course_id: this.schoolCourseId,
        student_id: studentId
      };
      try {
        await api.enrollment(data);
        this.snackBar.increment({
          show: true,
          msg: 'Aluno incluido com sucesso',
          color: 'success'
        });
        this.$emit('reload');
        this.loading = false;
        this.close();
      } catch (e) {
        const feedback = e?.response?.data?.message ?? 'Ocorreu um erro, tente novamente mais tarde';
        this.snackBar.increment({
          show: true,
          msg: feedback,
          color: 'warning'
        });
        this.loading = false;
      }
    }
  }
};
