import api from '@/api/api';
import { useSnackBar } from '@/stores/snackBar.js';
export default {
  name: 'EditSchoolCourse',
  props: {
    schoolCourseId: {
      type: Number,
      default: null
    },
    schoolCourseName: {
      type: String,
      default: ''
    },
    dialogEditSchoolCourse: {
      type: Boolean,
      default: false
    }
  },
  data: () => ({
    loading: false,
    dialog: false,
    name: ''
  }),
  computed: {
    snackBar() {
      return useSnackBar();
    }
  },
  watch: {
    schoolCourseName() {
      this.name = this.schoolCourseName;
    }
  },
  created() {
    this.name = this.schoolCourseName;
  },
  methods: {
    close() {
      this.$emit('close');
    },
    async update() {
      this.loading = true;
      const data = {
        name: this.name
      };
      try {
        await api.updateSchoolCourse(this.schoolCourseId, data);
        this.$emit('reload');
        this.snackBar.increment({
          show: true,
          msg: 'Turma atualizada com sucesso',
          color: 'success'
        });
        this.loading = false;
        this.$emit('close');
      } catch (e) {
        this.loading = false;
        const feedback = e?.response?.data?.message ?? 'Ocorreu um erro, tente novamente mais tarde';
        this.snackBar.increment({
          show: true,
          msg: feedback,
          color: 'warning'
        });
      }
    }
  }
};
