import axios from '@/api/axios'

export async function listStudents() {
  const request =  await axios.get('/api/students')
  console.log(request.data.data)
}

export async function createStudent(data) {
  await axios.post('/api/student', data)
}

export async function getStudent(id) {
  const request =  await axios.get('/api/student/' + id)
  console.log(request.data.data)
}

export async function updateStudent(id, data) {
  await axios.put('/api/student/' + id, data)
}

export async function deleteStudent(id) {
  await axios.delete('/api/student/' + id)
}

export async function enrollment(data) {
  await axios.post('/api/student/enrollment', data)
}

export async function unenrollment(id) {
  await axios.delete('/api/student/enrollment/' + id)
}

export default {
  listStudents,
  createStudent,
  getStudent,
  updateStudent,
  deleteStudent
}
