import api from '@/api/api';
import { useSnackBar } from '@/stores/snackBar.js';
export default {
  name: 'AddSubject',
  props: {
    schoolCourseId: {
      type: Number,
      default: null
    }
  },
  data: () => ({
    loading: false,
    dialogAddSubject: false,
    subjects: []
  }),
  computed: {
    snackBar() {
      return useSnackBar();
    }
  },
  watch: {},
  async created() {
    await this.listSubjects();
  },
  methods: {
    close() {
      this.dialogAddSubject = false;
    },
    async listSubjects() {
      this.loading = true;
      try {
        this.subjects = await api.listSubjects();
        this.loading = false;
      } catch (e) {
        const feedback = e?.response?.data?.message ?? 'Ocorreu um erro, tente novamente mais tarde';
        this.snackBar.increment({
          show: true,
          msg: feedback,
          color: 'warning'
        });
        this.loading = false;
      }
    },
    async addSubject(subjectId) {
      this.loading = true;
      const data = {
        school_course_id: this.schoolCourseId,
        subject_id: subjectId
      };
      try {
        await api.createSchoolCourseSubject(data);
        this.snackBar.increment({
          show: true,
          msg: 'Matéria incluida com sucesso',
          color: 'success'
        });
        this.$emit('reload');
        this.loading = false;
      } catch (e) {
        const feedback = e?.response?.data?.message ?? 'Ocorreu um erro, tente novamente mais tarde';
        this.snackBar.increment({
          show: true,
          msg: feedback,
          color: 'warning'
        });
        this.loading = false;
      }
    }
  }
};
