import { defineStore } from 'pinia'

export const useSnackBar = defineStore('snackBar', {
  state() {
    return {
      snackbar: {
        show: false,
        msg: ''
      }
    }
  },

  actions: {
    increment(value) {
      this.$state.snackbar = snackBars.concat(value)
    }
  },

  getters: {
    showSnackBar() {
      return this.snackbar
    }
  }
})
