import api from '@/api/api';
export default {
  name: 'DeleteStudents',
  props: {
    studentId: {
      type: Number,
      default: null
    },
    studentName: {
      type: String,
      default: ''
    },
    dialogDeleteStudent: {
      type: Boolean,
      default: false
    }
  },
  data: () => ({
    loading: false,
    name: ''
  }),
  methods: {
    close() {
      this.$emit('close');
    },
    async deleteStudentConfirm() {
      this.loading = true;
      try {
        await api.deleteStudent(this.studentId);
        this.$emit('reload');
        this.snackBar.increment({
          show: true,
          msg: 'Aluno apagado com sucesso',
          color: 'success'
        });
        this.loading = false;
        this.$emit('close');
      } catch (e) {
        this.loading = false;
        const feedback = e?.response?.data?.message ?? 'Ocorreu um erro, tente novamente mais tarde';
        this.snackBar.increment({
          show: true,
          msg: feedback,
          color: 'warning'
        });
      }
    }
  }
};
