import api from '@/api/api';
export default {
  name: 'EditSchoolCourse',
  props: {
    schoolCourseId: {
      type: Number,
      default: null
    },
    schoolCourseName: {
      type: String,
      default: ''
    },
    dialogEditSchoolCourse: {
      type: Boolean,
      default: false
    }
  },
  data: () => ({
    loading: false,
    dialog: false,
    name: ''
  }),
  computed: {},
  watch: {
    schoolCourseName() {
      this.name = this.schoolCourseName;
    }
  },
  created() {
    this.name = this.schoolCourseName;
  },
  methods: {
    close() {
      this.$emit('close');
    },
    async update() {
      this.loading = true;
      const data = {
        name: this.name
      };
      try {
        await api.updateSchoolCourse(this.schoolCourseId, data);
        this.$emit('reload');
        this.loading = false;
        this.$emit('close');
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
