import axios from '@/api/axios'

export async function listSchoolCourses() {
  const request =  await axios.get('/api/schoolcourses')
  console.log(request.data.data)
}

export async function createSchoolCourse(data) {
  await axios.post('/api/schoolcourse', data)
}

export async function getSchoolCourse(id) {
  const request =  await axios.get('/api/schoolcourse/' + id)
  console.log(request.data.data)
}

export async function updateSchoolCourse(id, data) {
  await axios.put('/api/schoolcourse/' + id, data)
}

export async function deleteSchoolCourse(id) {
  await axios.delete('/api/schoolcourse/' + id)
}

export default {
  listSchoolCourses,
  createSchoolCourse,
  getSchoolCourse,
  updateSchoolCourse,
  deleteSchoolCourse
}
