import api from '@/api/api';
export default {
  name: 'AddStudent',
  props: {
    schoolCourseId: {
      type: Number,
      default: null
    }
  },
  data: () => ({
    loading: false,
    dialogAddStudent: false,
    students: []
  }),
  async created() {
    await this.listStudents();
  },
  methods: {
    close() {
      this.dialogAddStudent = false;
    },
    async listStudents() {
      this.loading = true;
      try {
        this.students = await api.listStudents();
        this.loading = false;
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    },
    async addStudent(studentId) {
      this.loading = true;
      const data = {
        school_course_id: this.schoolCourseId,
        student_id: studentId
      };
      try {
        await api.enrollment(data);
        this.$emit('reload');
        this.loading = false;
        this.close();
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
