import api from '@/api/api';
export default {
  name: 'DeleteStudents',
  props: {
    studentId: {
      type: Number,
      default: null
    },
    studentName: {
      type: String,
      default: ''
    },
    dialogDeleteStudent: {
      type: Boolean,
      default: false
    }
  },
  data: () => ({
    loading: false,
    name: ''
  }),
  methods: {
    close() {
      this.$emit('close');
    },
    async deleteStudentConfirm() {
      this.loading = true;
      try {
        await api.deleteStudent(this.studentId);
        this.$emit('reload');
        this.loading = false;
        this.$emit('close');
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
