import api from '@/api/api';
export default {
  name: 'NewStudents',
  layout: 'Default',
  data: () => ({
    loading: false,
    dialog: false,
    name: ''
  }),
  computed: {},
  watch: {
    dialog() {
      if (this.dialog === false) {
        this.name = '';
      }
    }
  },
  created() {},
  methods: {
    async save() {
      this.loading = true;
      const data = {
        name: this.name
      };
      try {
        await api.createStudent(data);
        this.$emit('reload');
        this.dialog = false;
        this.loading = false;
      } catch (e) {
        this.loading = false;
        console.log(e);
      }
    }
  }
};
